CREATE TABLE Entry (id INTEGER PRIMARY KEY AUTOINCREMENT NOT NULL, userID INTEGER NOT NULL REFERENCES User (userID) ON DELETE CASCADE ON UPDATE CASCADE, academicTitle VARCHAR(0, 20), lastName VARCHAR (2, 20) NOT NULL, firstName VARCHAR (2, 30) NOT NULL, gender CHAR(1), dateOfBirth DATE NOT NULL, maidenName VARCHAR (2, 20), religion VARCHAR (2, 20), placeOfBirth VARCHAR (2, 40) NOT NULL, maritalStatus INTEGER NOT NULL, nationality VARCHAR (0, 255) NOT NULL, zmr CHAR (12), travelDocumentID VARCHAR (2, 20), travelDocumentNumber INTEGER, travelDocumentAuthority VARCHAR (2, 30), travelDocumentCountry VARCHAR (255), travelDocumentDate DATE, dateCreated DATE DEFAULT (datetime('now', 'localtime')) NOT NULL, r_street VARCHAR (2, 30) NOT NULL, r_housenumber VARCHAR (1, 4) NOT NULL, r_apartment VARCHAR (2, 20), r_door VARCHAR (1, 4), r_postalCode CHAR (4) NOT NULL, r_city VARCHAR (2, 20) NOT NULL, r_mainResidence BOOLEAN NOT NULL, r_mainResidenceStreet VARCHAR (255), r_mainResidenceHouseNumber VARCHAR(255), r_mainResidenceApartment VARCHAR(255), r_mainResidenceDoor VARCHAR(255), r_mainResidencePostalCode VARCHAR(255), r_mainResidenceCity VARCHAR(255), r_abroad BOOLEAN NOT NULL, r_abroadCountry VARCHAR (255), c_street VARCHAR (2, 30), c_houseNumber VARCHAR (1, 4), c_apartment VARCHAR (2, 20), c_door VARCHAR (1, 4), c_postalCode CHAR (4), c_city VARCHAR (2, 20), c_abroad BOOLEAN, c_abroadCountry VARCHAR (255), userDate DATE NOT NULL, CHECK (gender IN ('m', 'f')));
CREATE TABLE User (id INTEGER PRIMARY KEY AUTOINCREMENT NOT NULL, firstName VARCHAR (2, 30) NOT NULL, lastName VARCHAR (2, 20) NOT NULL, email VARCHAR(0, 255) NOT NULL, password VARCHAR(0, 255) NOT NULL, dateCreated DATE DEFAULT (datetime('now', 'localtime')));